parameters.jsCode:
// Analizar si el usuario pide enviar traducción por email
const message = $input.item.json.body.message || '';
const lowerMsg = message.toLowerCase();

// Extraer email del destinatario primero
const emailMatch = message.match(/([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/);
const toEmail = emailMatch ? emailMatch[1] : null;

// Extraer asunto si existe (ejemplo: "asunto: Mi mensaje" o "subject: My message")
const subjectMatch = message.match(/(?:asunto|subject):\s*(.+?)(?=\s+al\s+correo|\s+a\s+|$)/i);
const customSubject = subjectMatch ? subjectMatch[1].trim() : null;

// Detectar solicitud de email - más flexible
const emailKeywords = ['envía', 'envia', 'enviar', 'manda', 'mandar', 'envíame', 'enviame', 'enviá', 'envia'];
const translationKeywords = ['traducción', 'traduccion', 'email', 'correo', 'mail', 'mensaje'];

const hasEmailAction = emailKeywords.some(word => lowerMsg.includes(word));
const hasEmailMention = translationKeywords.some(word => lowerMsg.includes(word)) || toEmail !== null;

const isEmailRequest = (hasEmailAction && hasEmailMention) || (toEmail !== null && lowerMsg.length < 100);

return {
  json: {
    message: message,
    action: (isEmailRequest && toEmail) ? 'send_translation' : 'chat',
    toEmail: toEmail,
    customSubject: customSubject,
    debug: {
      hasEmailAction: hasEmailAction,
      hasEmailMention: hasEmailMention,
      emailFound: toEmail !== null
    }
  }
};

// Obtener la última fila del Sheets
const items = $input.all();

if (!items || items.length === 0) {
  throw new Error('No hay traducciones guardadas');
}

// Tomar la última fila directamente (la más reciente)
const lastRow = items[items.length - 1].json;

// Obtener el email y asunto del nodo Analyze Request
const analyzeData = $('Analyze Request').first().json;
const toEmail = analyzeData.toEmail || 'destinatario@example.com';
const customSubject = analyzeData.customSubject;

// Intentar leer los datos usando diferentes posibles nombres (incluyendo MAYÚSCULAS)
const originalText = lastRow['Texto Original'] || lastRow['TEXTO ORIGINAL'] || lastRow.B || lastRow['B'] || '';
const translation = lastRow['Traducción'] || lastRow['Traduccion'] || lastRow['TRADUCCION'] || lastRow.C || lastRow['C'] || '';
const targetLang = lastRow['Idioma Destino'] || lastRow['IDIOMA DESTINO'] || lastRow.D || lastRow['D'] || '';
const timestamp = lastRow['Fecha'] || lastRow['FECHA'] || lastRow.A || lastRow['A'] || '';
const model = lastRow['Modelo'] || lastRow['MODELO'] || lastRow.E || lastRow['E'] || '';

// Generar saludo por defecto según idioma destino si no hay asunto personalizado
const greetings = {
  'inglés': 'Hello!',
  'ingles': 'Hello!',
  'english': 'Hello!',
  'español': '¡Hola!',
  'espanol': '¡Hola!',
  'spanish': '¡Hola!',
  'francés': 'Bonjour!',
  'frances': 'Bonjour!',
  'french': 'Bonjour!',
  'alemán': 'Guten Tag!',
  'aleman': 'Guten Tag!',
  'german': 'Guten Tag!',
  'portugués': 'Olá!',
  'portugues': 'Olá!',
  'portuguese': 'Olá!',
  'italiano': 'Ciao!',
  'italian': 'Ciao!',
  'chino': '你好!',
  'chinese': '你好!',
  'japonés': 'こんにちは!',
  'japones': 'こんにちは!',
  'japanese': 'こんにちは!'
};

const defaultGreeting = greetings[targetLang.toLowerCase()] || 'Hello!';
const subject = customSubject || defaultGreeting;

return {
  json: {
    originalText: originalText,
    translation: translation,
    targetLang: targetLang,
    timestamp: timestamp,
    model: model,
    toEmail: toEmail,
    subject: subject
  },
  pairedItem: items[items.length - 1].pairedItem
};

